sys_script_include.script:
var GoogleDirErrorHandler = Class.create();
GoogleDirErrorHandler.prototype = {
    initialize: function() {
    },

	genericErrorHandler : function(statusCode,responseBody){
		responseBody =JSON.parse(responseBody);
		if(statusCode == 401){
				return "Insufficient Permissions.Login Required. Please Check Oauth Token and scope permission.";
			}
		if(statusCode == 403)
			return "Forbidden Request. Please Check Oauth Token and scope permission.";
		if(statusCode == 500)
			return  "Google Directory is not reachable.";
		if(statusCode == 400)
		{ 
			if(responseBody.error.message.indexOf( "Invalid Password")!=-1)
				{
					return "Invalid password";
				}
			return responseBody.error.message;
		}
		if(statusCode == 404)
			return responseBody.error.message;
		if(statusCode == 409)
		{
			return "Entity with this key already exist";
		}
		if(statusCode == 412)
			{
				return "Number of users limits exceeded";
			}
	},

    type: 'GoogleDirErrorHandler'
};
